<?php

/**
 * 
 * @param String $uri
 * @return String url
 * 
 */
function base_url($uri = '')
{
	$TRETA = new Treta_Engine();
	$requestUri = explode("/",$_SERVER['REQUEST_URI']);

	if(end($requestUri) == '')
	{
		array_pop($requestUri);
	}

	for($i=0 ; $i< count($requestUri);$i++)
	{
		if($requestUri[$i] == $TRETA->controller)
		{
			break;
		}
		else if($i == 0)
		{
			$finalUri = $requestUri[$i];
		}
		else
		{
			$finalUri = $finalUri."/".$requestUri[$i];
		}
	}
	if($finalUri[strlen($finalUri)-1] == "/")
	{
		unset($finalUri[strlen($finalUri)-1]);
	}
	else
	{
		$finalUri .= "/";
	}

	unset($TRETA);
	$protocol = ($_SERVER['HTTPS'] && $_SERVER['HTTPS'] != "off") ? "https" : "http";
	$uri = $uri != ''?$uri:'';
	return $protocol."://".$_SERVER['HTTP_HOST'].$finalUri.$uri;

}
/**
 * 
 * @param String $url
 * @param String $title
 * @param Array $attributes
 * @uses anchor("google.com", "google",array("target"=>"_blank","style"=>array("background"=>"#f00","color"=>"#fff")))
 * @return Retorna ancora de hiperlink
 */
function anchor($url,$title,$attributes = null)
{
if(is_array($attributes))
	{
		foreach ($attributes as $ind => $val)
		{
			if($ind == "style" && is_array($val))
			{
				foreach ($val as $style => $valStyle)
				{
					$estilo[] = $style.":".$valStyle."";

				}
			}
			else
			{
				$campo[] = $ind."='".$val."'";
			}
		}
		
		$attributes = is_array($campo)?implode(" ", $campo):"";
		$attributes .= isset($estilo)?" style=".implode(";", $estilo):null;
		
	}

	return "<a href='".$url."' ".$attributes.">".$title."</a>";
}
